mapper.select:
select o.id as id, o.order_no, o.remark, u.username, o.delivery_name, o.pay_money, o.status, oi.product_name as name, oi.number as num from sell_order o left join sys_user u on o.user_id = u.id left join sell_order_item oi on o.order_no = oi.order_no where o.shop_id = #{shopId}
and o.status = #{status}
and o.order_no like #{orderNo}

select o.id as id, o.order_no, o.shop_id, o.shop_logo, o.shop_name, o.status, o.pay_money, o.create_time, oi.product_name as name, oi.number as num from sell_order o left join sell_order_item oi on o.order_no = oi.order_no where o.user_id = #{userId}
and o.order_no like #{orderNo}
order By o.create_time desc
